_onInitialize(ExploreInitialEvent event, Emitter<ExploreState> emit, ) async  { emit(state.copyWith(searchController: TextEditingController())); emit(state.copyWith(exploreModelObj: state.exploreModelObj?.copyWith(exploreItemList: fillExploreItemList()))); } 
