import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';import '/core/app_export.dart';import '../models/trendingnowframe_item_model.dart';import '../models/artistsframe_item_model.dart';import '../models/topchartslist_item_model.dart';import 'package:dhiwise_flutter_music_app/presentation/home_page/models/home_model.dart';part 'home_event.dart';part 'home_state.dart';/// A bloc that manages the state of a Home according to the event that is dispatched to it.
class HomeBloc extends Bloc<HomeEvent, HomeState> {HomeBloc(HomeState initialState) : super(initialState) { on<HomeInitialEvent>(_onInitialize); }

_onInitialize(HomeInitialEvent event, Emitter<HomeState> emit, ) async  { emit(state.copyWith(homeModelObj: state.homeModelObj?.copyWith(trendingnowframeItemList: fillTrendingnowframeItemList(), artistsframeItemList: fillArtistsframeItemList(), topchartslistItemList: fillTopchartslistItemList()))); } 
List<TrendingnowframeItemModel> fillTrendingnowframeItemList() { return [TrendingnowframeItemModel(image: ImageConstant.imgImage, loremIpsumDolor: "Shades of Love - Ania Szarmach"), TrendingnowframeItemModel(image: ImageConstant.imgImage160x160, loremIpsumDolor: "Without You - The Kid LAROI"), TrendingnowframeItemModel(image: ImageConstant.imgImage1, loremIpsumDolor: "Save Your Tears\nThe Weeknd & Ari")]; } 
List<ArtistsframeItemModel> fillArtistsframeItemList() { return [ArtistsframeItemModel(arianaGrande: ImageConstant.imgImage2, artistsName: "Ariana Grande"), ArtistsframeItemModel(arianaGrande: ImageConstant.imgImage3, artistsName: "The Weeknd"), ArtistsframeItemModel(arianaGrande: ImageConstant.imgImage4, artistsName: "Acidrap")]; } 
List<TopchartslistItemModel> fillTopchartslistItemList() { return [TopchartslistItemModel(top: ImageConstant.imgImage5, price: "TOP 100", top1: ImageConstant.imgImage6, price1: "TOP 50", top2: ImageConstant.imgImage7, price2: "TOP 100", price3: "TOP 100 - Global")]; } 
 }
