class PodcastDetailsBloc extends Bloc<PodcastDetailsEvent, PodcastDetailsState> {PodcastDetailsBloc(PodcastDetailsState initialState) : super(initialState) { on<PodcastDetailsInitialEvent>(_onInitialize); }

_onInitialize(PodcastDetailsInitialEvent event, Emitter<PodcastDetailsState> emit, ) async  { emit(state.copyWith(podcastDetailsModelObj: state.podcastDetailsModelObj?.copyWith(podcastdetailsItemList: fillPodcastdetailsItemList()))); } 
